  }
  .stat.percentage {
    border-color: #555;
  }
  .section-8 .tag-link {
    background: #111;
    border-color: #444;
    color: #ccc !important;
  }
  .section-8 .tag-link:hover {
    background: #222;
    color: #ddd !important;
  }
  .section-3 .illustration {
    border-top-color: #666;
  }
  .mods-item, .league-container {
    border-color: #444;
  }
  .bc-black-3 {
    border-color: #444 !important;
  }
  .fc-blue-600 {
    color: rgb(65, 131, 196) !important;
  }
  .introBlock .illustration .img1, img[src*="db.svg"] {
    filter: invert(1) hue-rotate(180deg) contrast(90%) brightness(300%);
    mix-blend-mode: exclusion;
  }
  img[src="https://cdn.sstatic.net/stackexchange/Img/performance/svg/server.svg?v=8469d56f8b0e"] {
    filter: invert(1) hue-rotate(180deg) contrast(350%) brightness(60%);
    mix-blend-mode: exclusion;
  }
  a[href*="stackoverflow"] img[src*="Img/about/logo-sotalent.svg"],
  a[href*="stackoverflow"] img[src*="Img/about/logo-so.svg"] {
    filter: invert(100%) hue-rotate(180deg) !important;
  }

options.value:
background-repeat: repeat !important;
  background-size: auto !important;
  background-position: left top !important;

background-repeat: no-repeat !important;
  background-size: cover !important;
  background-position: center top !important;

