    body {
        border: none !important;
        font-family: 'Source Sans Pro', sans-serif !important;
        font-size: 18px !important;
        background: #2c2f33 !important;
    }
    #content {
        margin: 0 auto !important;
        border: none !important;
        font-family: 'Source Sans Pro' !important;
        font-size: 1rem !important;
        line-height: 80% !important;
        max-width: 920px !important;
        background: #2c2f33 !important;
        color: #ccc !important;
    }
    a {
        text-decoration: none !important;
    }
    a:hover {
        text-decoration: underline !important;
    }
    /* top nav */
    #mw-page-base {
        height: 90px !important;
        margin: 0 auto !important;
        background: #23272a !important;
        /* background: #CAC8C4 !important; */
    }
    #mw-navigation {
        position: absolute !important;
        top: 0 !important;
        left: 0 !important;
        right: 0 !important;
    }
    #mw-head {
        position: relative !important;
        max-width: 870px !important;
        height: 90px !important;
        margin: 0 auto !important;
    }
    #mw-head a,
    #mw-head ul,
    #mw-head li,
    #mw-head span,
    #mw-head .vectorTabs {
        background: none !important;
        border: none !important;
    }
    #mw-head > * {
        position: absolute !important;
        font-size: 16px !important;
        margin: 0 !important;
        padding: 0 !important;
    }
    #mw-head #left-navigation {
        top: 50px !important;
        left: -30px !important;
        padding: 0 !important;
    }
    #mw-head #right-navigation {
        top: 50px !important;
        right: 0px !important;
        padding: 0 !important;
    }
    #mw-head #p-personal {
        top: 0px !important;
        right: -30px !important;
        padding: 5px 0px 0 0 !important;
        font-size: 14px !important;
    }
    #mw-head li {
        opacity: 0 !important;
        transition: all 0.45s ease-out !important;
    }
    #mw-head:hover li {
        opacity: 1 !important;
        transition: all 0.13s ease-in !important;
    }
    #mw-head a,
    #mw-head a:visited {
        color: #f0f0f0 !important;
    }
    #mw-head #searchform > div {
        position: absolute !important;
        top: -32px !important;
        right: -30px !important;
        margin-top: 13px !important;
        margin-left: 15px !important;
        border: none !important;
        background: #111 !important;
        width: 345px !important;
        padding: 0 0 7px !important;
    }
    #mw-head #searchform > div input#searchInput {
        padding: 4px 0 6px 8px !important;
        font-size: 1rem;
        color: #ccc !important;
    }
    #mw-head #p-cactions,
    #mw-head #ca-watch {
        display: none !important;
    }
    /* logo */
    #mw-head:before {
        position: relative !important;
        width: 120px !important;
        height: 120px !important;
        top: 30px !important;
        left: -25px !important;
        content: 'Wikipedia' !important;
        font-family: 'Source Serif Pro', serif !important;
        /*font-style: italic !important;*/
        font-weight: normal !important;
        font-size: 28px !important;
        text-transform: uppercase !important;
        letter-spacing: 0px !important;
        color: #eee !important;
    }
    /* left nav */
    #mw-navigation #mw-panel {
        position: fixed !important;
        font-size: 18px !important;
        top: 0px !important;
        left: -255px !important;
        width: 160px !important;
        height: 100% !important;
        overflow-y: auto !important;
        padding: 0 0 0 100px !important;
        background: #23272a !important;
        box-shadow: 1px 0 3px rgba(0, 0, 0, 0.25) !important;
        transition: all 0.66s ease-in-out !important;
        opacity: 0 !important;
    }
    #mw-navigation #mw-panel:hover {
        left: -100px !important;
        transition: all 0.2s ease-in-out !important;
        opacity: 1 !important;
    }
    #mw-navigation #mw-panel a {
        color: #eee !important;
    }
    #mw-navigation #p-navigation,
    #mw-navigation .first {
        margin-bottom: 0 !important;
        margin-top: 7px !important;
    }
    #mw-navigation #p-lang:before {
        content: "" !important;
        font-size: 0 !important;
        display: inline-block !important;
        height: 0px !important;
        width: 100% !important;
        border-top: 1px solid rgba(0, 0, 0, 0.2) !important;
        border-bottom: 1px solid rgba(255, 255, 255, 0.75) !important;
    }
    #mw-navigation #p-lang {
        margin-top: -15px !important;
    }
    #mw-navigation #p-lang-label {
        color: #fff;
        font-size: 1.2rem;
    }
    #mw-navigation #p-lang .uls-settings-trigger {
        margin-right: 8px !important;
        margin-top: 6px !important;
    }
    #mw-navigation > #mw-panel > div:not(#p-lang):not(#p-navigation):not(.first),
    #mw-navigation #p-lang .wbc-editpage {
        display: none !important;
    }
